import './Content.scss'
import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'
const Content = () => {
    const [joke,setJoke] = useState('')
    const [err,setErr] = useState('')
    let hasReachedEnd = false;
    const getNextJoke = async () => {
        if (hasReachedEnd) {
            setJoke("That's all the jokes for today! Come back another day!");
            return;
        }
    
        const apiUrl = "http://localhost:3001/next";
        const lastJokeId = joke ? joke.id : null; // Nếu joke tồn tại, lấy id của joke, ngược lại sử dụng null
    
        try {
            const response = await axios.post(apiUrl, { lastJokeId });
            if (response && response.data) {
                const responseData = response.data;
                if (typeof responseData === 'string') {
                    // Nếu dữ liệu trả về là một chuỗi, đánh dấu đã đến cuối danh sách
                    hasReachedEnd = true;
                    setJoke("That's all the jokes for today! Come back another day!");
                } else {
                    // Nếu không phải là chuỗi, hiển thị truyện cười và cập nhật lastJokeId
                    setJoke(responseData);
                }
            } else {
                console.error('Error fetching next joke');
            }
        } catch (error) {
            console.log(error);
        }
    };
    
    useEffect(() => {
        getNextJoke();
    },[])
    console.log('abc')
    return (
        <div className = "content">
            {joke && ( 
                <p className = "story">{joke.content}</p>
        
            )}
            <hr className='hr' size = "1px"/>
            <div className = "button">
            <button className = "fun-button" onClick = {getNextJoke}>This is Funny!</button>
            <button className = "no-button" onClick = {getNextJoke}>This is not funny.</button>
             </div>
            
        </div>
    )
}
export default Content
